mapper.select:
SELECT 

FROM dm_user a

WHERE a.id = #{id}

SELECT

FROM dm_user a

WHERE a.phone_number = #{phoneNumber}

SELECT
FROM dm_user a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{nickname}||'%' '%'+#{nickname}+'%' concat('%',#{nickname},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM dm_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dm_user SET
    nickname = #{nickname},
	password = #{password},
	head_portrait = #{headPortrait},
	token = #{token}
WHERE id = #{id}
