mapper.select:
SELECT

FROM dm_yunbiji a

WHERE a.id = #{id}

mapper.update:
UPDATE dm_yunbiji SET
	biji_size = #{bijiSize},
	biji = #{biji},
	biji_image = #{bijiImage},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE dm_yunbiji SET
	biji_name = #{bijiName},
	update_date = #{updateDate}
WHERE id = #{id}
